INSERT INTO `pages` (`id`, `title`, `body`, `img`, `keywords`, `description`, `created`, `modified`, `alias`) VALUES
(1, 'About us', '<p>Юзабилити &mdash; важный инструмент, обеспечивающий пользователям удобство работы с сайтом, а вам высокий процент продаж. Задача юзабилити &mdash; удержание клиента на сайте и стимулирование его на совершение заказа.</p>\r\n\r\n<p>Юзабилити-тестирование нужно проводить в том случае, если у вас на сайте достаточное число пользователей в день, но заказов они не совершают. В чем дело? Почему сайт не довел клиента до совершения сделки или звонка в компанию?</p>\r\n\r\n<p>Все дело в том, что пользователю не удобно на вашем сайте. Это мешает ему довести сессию до конца, и он просто покидает сайт, так ничего и не купив.</p>\r\n\r\n<p>Аудит сайта позволяет определить поведенческий фактор пользователя и сделать интерфейс вашего сайта &ldquo;дружественным&rdquo;. Благодаря современным технологиям можно без труда проводить анализ сайта и получать данные, отслеживая даже взгляд пользователя. Технология eye-tracking, с помощью специального устройства, позволяет отслеживать движения глаз человека при просматривании web-сайта.</p>\r\n', '8a70c3e207381763a4ac28c537fdb4a4.jpg', 'keywords', 'description', '2016-02-15 00:00:00', '2016-02-16 18:12:04', 'about'),
(2, 'Why choose us?', '<p>text</p>', '', 'keywords', 'description', '2016-02-15 00:00:00', '2016-02-15 00:00:00', 'why_choose_us'),
(3, 'What we offer', '<p>text</p>', '', 'keywords', 'description', '2016-02-15 00:00:00', '2016-02-15 00:00:00', 'what_we_offer'),
(4, 'Our team', '<p>text</p>', '', 'keywords', 'description', '2016-02-15 00:00:00', '2016-02-15 00:00:00', 'our_team'),
